/** @format */

import React from 'react';
import { Link } from 'react-router-dom';
import { useRecoilValue } from 'recoil';
import { isLoggedInState, userInfoState } from '../recoilAtoms';

export default function Header() {
  const userInfo = useRecoilValue(userInfoState);
  const isLoggedIn = useRecoilValue(isLoggedInState);
  return (
    <header className='fixed top-0 left-0 right-0 p-3 bg-indigo-500 text-slate-100 z-10'>
      <nav className='flex gap-3 pl-5 pr-5 w-3/6'>
        <div>
          <Link to={'/'}>홈</Link>
        </div>
        <div className='grow'></div>
        <div>
          {isLoggedIn ? (
            <div className='flex gap-3'>
              <Link to={'/mypage'}>{userInfo.displayName}</Link>
              <Link to={'/serch'}>검색</Link>
            </div>
          ) : (
            <div className='flex gap-3'>
              <Link to={'/login'}>로그인</Link>
              <Link to={'/join'}>회원가입</Link>
            </div>
          )}
        </div>
      </nav>
    </header>
  );
}
