/** @format */

import React, { useEffect, useState } from 'react';
import tmdbAxiosConfig from '../api/tmdbAxiosConfig';
import Card from '../components/Card';

export default function Serch() {
  const [searchValue, setSerchValue] = useState('');
  const [searchMovies, setSearchMovies] = useState([]);
  const fetchSerchMovie = async () => {
    const result = await tmdbAxiosConfig.get(
      `/search/multi?include_adult=false&query=${searchValue}`
    );
    console.log(result.data.results);
    setSearchMovies(result.data.results);
  };
  useEffect(() => {
    fetchSerchMovie();
  }, [searchValue]);
  return (
    <div className='flex flex-col items-center justify-center gap-3 pt-3 mt-12'>
      <div className='w-10/12 sm:w-96'>
        <input
          className='border focus:border-indigo-400 w-full p-2 rounded-md'
          type='text'
          onChange={(e) => {
            setSerchValue(e.target.value);
          }}
        />
      </div>
      <div className='w-10/12 flex flex-wrap items-center justify-center gap-4'>
        {searchMovies.map((el, i) => {
          return (
            <>
              <div key={i} className='w-64 h-80 text-center border shadow-md'>
                <img
                  className='w-full h-5/6'
                  src={`https://www.themoviedb.org/t/p/original${
                    el.backdrop_path || el.poster_path
                  }`}
                />
                <div className='p-3'>{el.title || el.name}</div>
              </div>
            </>
          );
        })}
      </div>
    </div>
  );
}
