/** @format */

import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import tmdbAxiosConfig from '../api/tmdbAxiosConfig';

export default function Create() {
  const { mediaType, contentId } = useParams();
  const [content, setContent] = useState({});
  const fetchContent = async () => {
    try {
      const result = await tmdbAxiosConfig.get(`/${mediaType}/${contentId}`);
      console.log(result);
      setContent(result.data);
    } catch (err) {
      console.log(err);
    }
  };
  useEffect(() => {
    fetchContent();
  }, []);

  return (
    <div className='w-full flex flex-col justify-center items-center text-center'>
      <div className='w-6/12 flex flex-col justify-center items-center'>
        <h1 className='text-4xl py-10'>{content.title || content.name}</h1>
        <div className='py-5 w-full'>
          <input
            className='w-full text-center'
            placeholder='한줄평 적기'
            type='text'
          />
        </div>
        <div className='w-full py-5'>
          <textarea
            className='w-full text-center h-80'
            placeholder='뇌절 하시겠습니까'
          ></textarea>
        </div>
        <div>
          <button
            type='button'
            className='top-1.5 right-3 text-white text-sm rounded-md bg-red-400 w-10 h-6 hover:bg-red-500'
          >
            인증
          </button>
        </div>
      </div>
    </div>
  );
}
