/** @format */

import React, { useState } from 'react';
import FormInput from '../components/FormInput';
import axios from '../api/axios';

export default function Join() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setContirmPassword] = useState('');
  const [displayName, setDisplayName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState();

  const FormItems = [
    {
      value: email,
      setValue: setEmail,
      type: 'email',
      placeholder: '이메일을 입력해주세요',
    },
    {
      value: password,
      setValue: setPassword,
      type: 'password',
      placeholder: '비밀번호를 입력해주세요',
    },
    {
      value: confirmPassword,
      setValue: setContirmPassword,
      type: 'password',
      placeholder: '비밀번호 확인',
    },
    {
      value: displayName,
      setValue: setDisplayName,
      type: 'text',
      placeholder: '이름을 입력해주세요',
    },
    {
      value: displayName,
      setValue: setDisplayName,
      type: 'text',
      placeholder: '이름을 입력해주세요',
    },
  ];
  const isJoin = async () => {
    try {
      const result = await axios.post('/auth/join', {
        email: email,
        password: password,
        displayName: displayName,
      });
      console.log('가입요청성공 : ', result.data.message);
    } catch (error) {
      console.log('가입요청실패 : ', error);
    }
  };

  return (
    <div>
      <div className='flex flex-col h-screen justify-center items-center'>
        <div>
          <img
            className='mx-auto h-10 w-auto'
            src='https://tailwindui.com/img/logos/mark.svg?color=indigo&shade=600'
            alt='Your Company'
          />
          <h2 className='mt-5 text-center text-2xl font-bold leading-9 tracking-tight text-gray-900'>
            Sign up
          </h2>
        </div>

        <form className='w-5/6 md:w-80 mt-5 space-y-6' action='#' method='POST'>
          {FormItems.map((el, i) => {
            return (
              <FormInput
                key={i}
                value={el.value}
                setValue={el.setValue}
                type={el.type}
                placeholder={el.placeholder}
              />
            );
          })}

          <div>
            <button
              onClick={isJoin}
              type='button'
              className='flex w-full justify-center rounded-md bg-indigo-600 px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600'
            >
              Sign in
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
