/** @format */

import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import tmdbAxiosConfig from '../api/tmdbAxiosConfig';
import axiosConfig from '../api/axiosConfig';
import { userInfoState } from '../recoilAtoms';
import { useRecoilValue } from 'recoil';
import StarIcon from '../icons/StarIcon';

export default function Create() {
  const userInfo = useRecoilValue(userInfoState);
  const { mediaType, contentId } = useParams();
  const [content, setContent] = useState({});
  const [review, setReview] = useState('');
  const [addReview, setAddReview] = useState('');
  const [grade, setGrade] = useState('');
  const [stars, setStars] = useState([0, 0, 0, 0, 0]);
  const sendReview = async () => {
    console.log(
      `https://www.themoviedb.org/t/p/original/${content.poster_path}`,
      userInfo.id,
      userInfo.displayName,
      content.name,
      content.id
    );
    const reviewData = {
      userId: userInfo.id,
      userName: userInfo.displayName,
      review: review,
      addReview: addReview,
      grade: grade,
      contentImg: `https://www.themoviedb.org/t/p/original${content.poster_path}`,
      contentName: content.title || content.name,
      contentId: contentId,
      contentType: mediaType,
    };
    console.log(reviewData);

    try {
      const result = await axiosConfig.post(
        'post/create',
        { reviewData },
        { withCredentials: true }
      );
      console.log(result);
    } catch (err) {
      console.log(err);
    }
  };
  const fetchContent = async () => {
    try {
      const result = await tmdbAxiosConfig.get(`/${mediaType}/${contentId}`);
      console.log(result);
      setContent(result.data);
    } catch (err) {
      console.log(err);
    }
  };
  useEffect(() => {
    fetchContent();
  }, []);

  return (
    <div className='w-full flex flex-col justify-center items-center text-center'>
      <div className='w-5/12 flex flex-col justify-center items-center'>
        <h1 className='text-4xl py-10'>{content.title || content.name}</h1>
        {/* <input
          onChange={(e) => {
            setGrade(e.target.value);
          }}
          className='text-center'
          type='text'
          placeholder='평점'
        /> */}
        <div>
          <StarIcon />
        </div>
        <div className='py-5 w-full'>
          <input
            className='w-full text-center bg-slate-100'
            placeholder='한줄평 적기'
            type='text'
            value={review}
            onChange={(e) => {
              setReview(e.target.value);
            }}
          />
        </div>
        <div className='w-full py-5'>
          <textarea
            className='w-full text-center h-80 bg-slate-100'
            placeholder='뇌절 하시겠습니까'
            value={addReview}
            onChange={(e) => {
              setAddReview(e.target.value);
            }}
          ></textarea>
        </div>
        <div className='w-full flex'>
          <div className='grow'></div>
          <button
            onClick={sendReview}
            type='button'
            className='text-white text-sm rounded-md bg-red-400 w-10 h-6 hover:bg-red-500'
          >
            발행
          </button>
        </div>
      </div>
    </div>
  );
}
