/** @format */

import React, {
  useDeferredValue,
  useEffect,
  useState,
  useTransition,
} from 'react';
import tmdbAxiosConfig from '../api/tmdbAxiosConfig';
import Card from '../components/Card';

export default function Serch() {
  const [searchValue, setSerchValue] = useState('');
  const [searchMovies, setSearchMovies] = useState([]);
  const [isPending, startTransition] = useTransition();
  const fetchSearchMovie = async () => {
    const result = await tmdbAxiosConfig.get(
      `/search/multi?include_adult=false&query=${searchValue}`
    );
    console.log(result.data.results);

    setSearchMovies(result.data.results);
  };
  useEffect(() => {
    fetchSearchMovie();
    console.log(searchValue);
  }, [searchValue]);

  return (
    <div className='flex flex-col items-center justify-center gap-5 pt-5 mt-12'>
      <div className='w-11/12 sm:w-96 relative'>
        <input
          className='border focus:border-red-400 focus:bg-slate-50 w-full p-3 rounded-md'
          type='text'
          onChange={(e) => {
            setSerchValue(e.target.value);
          }}
        />
        <button
          type='button'
          className='absolute top-1.5 right-3 text-white text-sm rounded-md bg-red-400 w-10 h-6 hover:bg-red-500 z-10'
        >
          인증
        </button>
      </div>
      <div className='w-11/12 flex flex-wrap items-center justify-center gap-3'>
        {searchMovies.map((content, i) => {
          return <Card key={i} content={content} />;
        })}
      </div>
    </div>
  );
}
