/** @format */

import axios from 'axios';
import { dotenv } from 'dotenv';

const instance = axios.create({
  baseURL: 'https://api.themoviedb.org/3',
  params: {
    api_key: process.env.TMDB_API_KEY,
    language: 'ko-KR',
  },
});

export default instance;
