/** @format */

import React, { useEffect, useState } from 'react';
import tmdbAxiosConfig from '../api/tmdbAxiosConfig';

export default function Serch() {
  const [searchValue, setSerchValue] = useState('');
  const [searchMovies, setSearchMovies] = useState([]);
  const fetchSerchMovie = async () => {
    const result = await tmdbAxiosConfig.get(
      `/search/multi?include_adult=false&query=${searchValue}`
    );
    console.log(result.data.results);
    setSearchMovies(result.data.results);
  };
  useEffect(() => {
    fetchSerchMovie();
  }, [searchValue]);
  return (
    <div className='flex flex-col items-center justify-center'>
      <div className='w-10/12 sm:w-96'>
        <input
          className='border w-full'
          type='text'
          onChange={(e) => {
            setSerchValue(e.target.value);
          }}
        />
      </div>
      <div className='w-10/12 flex flex-wrap'>
        {searchMovies.map((el) => {
          return <div className='bg-black'></div>;
        })}
      </div>
    </div>
  );
}
