/** @format */

import axios from 'axios';
const api = process.env.TMDB_API_KEY;

const instance = axios.create({
  baseURL: 'https://api.themoviedb.org/3',
  params: {
    api_key: api,
    language: 'ko-KR',
  },
});

export default instance;
