/** @format */

import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import tmdbAxiosConfig from '../api/tmdbAxiosConfig';
import axiosConfig from '../api/axiosConfig';
import { userInfoState } from '../recoilAtoms';
import { useRecoilValue } from 'recoil';
import StarIcon from '../icons/StarIcon';

export default function Create() {
  const navigaror = useNavigate();
  const userInfo = useRecoilValue(userInfoState);
  const { mediaType, contentId } = useParams();
  const [content, setContent] = useState({});
  const [review, setReview] = useState('');
  const [addReview, setAddReview] = useState('');
  const [grade, setGrade] = useState(1);
  const stars = [1, 2, 3, 4, 5];

  const sendReview = async () => {
    const reviewData = {
      userId: userInfo.id,
      userName: userInfo.displayName,
      review: review,
      addReview: addReview,
      grade: grade,
      contentImg: `https://www.themoviedb.org/t/p/original${content.poster_path}`,
      contentName: content.title || content.name,
      contentId: contentId,
      contentType: mediaType,
    };

    try {
      const result = await axiosConfig.post(
        'post/create',
        { reviewData },
        { withCredentials: true }
      );
      navigator('/');
      console.log(result.data.message);
    } catch (err) {
      console.log(err);
    }
  };
  const fetchContent = async () => {
    try {
      const result = await tmdbAxiosConfig.get(`/${mediaType}/${contentId}`);

      setContent(result.data);
    } catch (err) {
      console.log(err);
    }
  };
  useEffect(() => {
    fetchContent();
  }, []);

  const isGrade = (star) => {
    setGrade(star);
  };

  return (
    <div className='w-full flex flex-col justify-center items-center text-center'>
      <div className='w-10/12 sm:w-6/12 flex flex-col justify-center items-center'>
        <h1 className='text-4xl pt-10 pb-5'>{content.title || content.name}</h1>
        {/* <input
          onChange={(e) => {
            setGrade(e.target.value);
          }}
          className='text-center'
          type='text'
          placeholder='평점'
        /> */}
        <div className='flex items-center justify-center gap-1'>
          {stars.map((star, i) => {
            return (
              <div
                key={i}
                onClick={() => {
                  isGrade(star, i);
                }}
              >
                <StarIcon color={'yellow'} />
              </div>
            );
          })}
        </div>
        <div className='pt-5'>
          <span>나 {userInfo.displayName}의 평점</span>
          <span className='text-red-400 pl-5'>무려 {grade}점!</span>
        </div>
        <div className='py-5 w-full'>
          <input
            className='w-full text-center bg-slate-100 p-2 rounded-md'
            placeholder='한줄평 적기 (적어야 됨)'
            type='text'
            value={review}
            onChange={(e) => {
              setReview(e.target.value);
            }}
          />
        </div>
        <div className='w-full py-5'>
          <textarea
            className='w-full text-center h-80 bg-slate-100 p-2 rounded-md'
            placeholder='뇌절 하시겠습니까 (안해도 됨)'
            value={addReview}
            onChange={(e) => {
              setAddReview(e.target.value);
            }}
          ></textarea>
        </div>
        <div className='w-full flex'>
          <div className='grow'></div>
          <button
            onClick={sendReview}
            type='button'
            className='text-white text-sm rounded-md bg-red-400 w-10 h-6 hover:bg-red-500'
          >
            발행
          </button>
        </div>
      </div>
    </div>
  );
}
