/** @format */

import React, { useEffect, useState } from 'react';
import tmdbAxiosConfig from '../api/tmdbAxiosConfig';
import { useParams } from 'react-router-dom';

export default function Detail({}) {
  const { mediaType, movieId } = useParams();
  const [content, setContent] = useState({});
  const fetchMovie = async () => {
    try {
      const result = await tmdbAxiosConfig.get(`/${mediaType}/${movieId}`);
      console.log(result);
      setContent(result.data);
    } catch (err) {
      console.log(err);
    }
  };
  useEffect(() => {
    fetchMovie();
  }, []);
  return (
    <div className='w-11/12'>
      <h1 className='text-2xl'>{content.title || content.name}</h1>
      <div className='h-80'>
        <img
          className='w-full h-full'
          src={`https://www.themoviedb.org/t/p/original/${content.poster_path}`}
          alt=''
        />
      </div>
      <div className='flex'>
        <div className='grow'></div>
        <button>{content.title || content.name} 한줄평 쓰기</button>
      </div>
    </div>
  );
}
